ayer":"account taking the staking risk (can be a different account than the one controlling the gems)"}},"CommitmentRevealed(address,uint24,bytes24,(uint64,uint8)[],bytes24,uint256)":{"params":{"commitmentHash":"the hash of the moves","epoch":"epoch number on which this commit belongs to","furtherMoves":"hash of further moves, unless bytes32(0) which indicate end.","moves":"the moves","player":"account who commited"}},"CommitmentVoid(address,uint24,uint256,bytes24)":{"params":{"amountBurnt":"amount of token to burn","epoch":"epoch number on which this commit belongs to","furtherMoves":"hash of further moves, unless bytes32(0) which indicate end.","player":"the account that made the commitment"}},"MoveProcessed(uint64,address,uint8,uint8)":{"params":{"newColor":"color that takes over","oldColor":"previous color of the cell","player":"account making the move","position":"cell at which the move take place"}},"ReserveDeposited(address,uint256,uint256)":{"params":{"amountDeposited":"the number of tokens deposited","newAmount":"the number of tokens in reserver as a result","player":"account receiving the token in the reserve"}},"ReserveWithdrawn(address,uint256,uint256)":{"params":{"amountWithdrawn":"the number of tokens withdrawnn","newAmount":"the number of tokens in reserver as a result","player":"account withdrawing the tokens"}}},"kind":"dev","methods":{"acknowledgeMissedReveal(address,bytes32,(uint64,uint8)[],bytes24)":{"params":{"furtherMoves":"if moves cannot be contained in one tx, further moves are represented by a hash to reveal too","moves":"the actual moves","player":"the account who committed the move","secret":"the secret used to make the commit"}},"addToReserve(uint256,(uint256,uint256,uint8,bytes32,bytes32))":{"params":{"permit":"permit EIP2612, .value = zero if not needed","tokensAmountToAdd":"amount of tokens to add"}},"makeCommitment(bytes24,address)":{"params":{"commitmentHash":"the hash of the moves","payee":"address to send ETH to along the commitment. Can be used to pay for reveal"}},"makeCommitmentWithExtraReserve(bytes24,uint256,(uint256,uint256,uint8,bytes32,bytes32),address)":{"params":{"commitmentHash":"the has of the moves","payee":"address to send ETH to along the commitment. Can be used to pay for reveal","permit":"permit EIP2612, value = zero if not needed","tokensAmountToAdd":"amount of tokens to add to the reserve. the resulting total must be enough to cover the moves"}},"poke(uint64)":{"params":{"position":"the cell position"}},"pokeMultiple(uint64[])":{"params":{"positions":"cell positions to collect from"}},"reveal(address,bytes32,(uint64,uint8)[],bytes24,bool,address)":{"params":{"furtherMoves":"if moves cannot be contained in one tx, further moves are represented by a hash to reveal too  Note that you have to that have enough moves (specified by MAX_NUM_MOVES_PER_HASH = 32)","moves":"the actual moves","payee":"address to send ETH to along the reveal","player":"the account who committed the move","secret":"the secret used to make the commit","useReserve":"whether the tokens are taken from the reserve or from approvals.  This allow player to keep their reserve intact and use it on their next move.  Note that this require the Stratagems contract to have enough allowance."}},"withdrawFromReserve(uint256)":{"params":{"amount":"number of tokens to withdraw"}}},"version":1},"userdoc":{"errors":{"CanStillReveal()":[{"notice":"Player have to reveal if they can Stratagems will prevent them from acknowledging missed reveal if there is still time to reveal."}],"CommitmentHashNotMatching()":[{"notice":"Player have to reveal their commitment using the exact same move values  If they provide different value, the commitment hash will differ and Stratagems will reject their reveal."}],"GameNotStarted()":[{"notice":"Game has not started yet, can't perform any action"}],"InCommitmentPhase()":[{"notice":"When in Commit phase, player can make new commitment but they cannot reveal their move yet."}],"InRevealPhase()":[{"notice":"When in Reveal phase, it is not possible to commit new moves or cancel previous commitment  During Reveal phase, players have to reveal their commitment, if not already done."}],"InvalidEpoch()":[{"notice":"Player can only reveal their move in the same epoch they commited.abi  If a player reveal later it can only do to minimize the reserve burn cost by calling : `acknowledgeMissedReveal`"}],"InvalidFurtherMoves()":[{"notice":"Player can make arbitrary number of moves per epoch. To do so they group moves into (MAX_NUM_MOVES_PER_HASH = 32) moves  This result in a recursive series of hash that they can then submit in turn while revealing.  The limit  (MAX_NUM_MOVES_PER_HASH = 32) ensure a reveal batch fits in a block."}],"NothingToReveal()":[{"notice":"Player can only reveal moves they commited."}],"PreviousCommitmentNotRevealed()":[{"notice":"Previous commitment need to be revealed before making a new one. Even if the corresponding reveal phase has passed.\  It is also not possible to withdraw any amount from reserve until the commitment is revealed.\If player lost the information to reveal, it can acknowledge failure which will burn all its reserve.\"}],"ReserveTooLow(uint256,uint256)":[{"notice":"to make a commitment you always need at least one `config.numTokensPerGems` amount in reserve  Player also need one `config.numTokensPerGems`  per moves during the Reveal phase."}]},"events":{"CommitmentCancelled(address,uint24)":{"notice":"A player has cancelled its current commitment (before it reached the reveal phase)"},"CommitmentMade(address,uint24,bytes24)":{"notice":"A player has commited to make a move and reveal it on the reveal phase"},"CommitmentRevealed(address,uint24,bytes24,(uint64,uint8)[],bytes24,uint256)":{"notice":"Player has revealed its previous commitment"},"CommitmentVoid(address,uint24,uint256,bytes24)":{"notice":"A player has canceled a previous commitment by burning some tokens"},"MoveProcessed(uint64,address,uint8,uint8)":{"notice":